INSERT INTO `hotelservice` VALUES ('2249', '11', '7天酒店北京奥运村店', '经济型', '亚运村商圈', '北京市原奥体东门往东300米安苑路路北', '230', '连锁品牌（7天酒店）、2007年开业、118间客房、层高6层', '酒店是距奥运会主会场鸟巢和水立方及国家会议中心最近的7天分店；是距首都机场、北京国际会议中心和中国国际展览中心（新馆）三方相距最近的7天分店，更有繁盛中难得安静舒服的房间；步行8分钟到地铁5号线和地铁10号线可让您方便到达北京站、故宫、天安门、清华、北大、后海、雍和宫、颐和园、长城等名胜古迹、旅游景点；周边有全聚德烤鸭、便宜坊烤鸭、靠近中日友好医院和对外经济贸易大学；分店可以接待外籍人士、香港、澳门、台湾、华侨等持有非中国大陆身份证人士。', '酒店各处提供wifi', '酒店提供收费停车位', '宽带上网、空调、吹风机、24小时热水、暖气', '接待外宾', '无', '010-64892088', 'http://hotel.qunar.com/city/beijing_city/dt-2249/', 'http://userimg.qunar.com/imgs/201409/12/66I5P26tADOSr6-t6480s.jpg');
