INSERT INTO `hotelservice` VALUES ('12209', '11', '北京福临阁宾馆', '二星级/其他', '西单商圈', '北京市西城区大六部口9号', '193', '2010年开业、27间客房、层高2层', '北京福临阁宾馆座落于西城区大六部口，毗邻西单、天安门、国家大剧院，门口有公交车和地铁，地处优越地段，交通非常便利。宾馆拥有各色房间，其中包括商务双人房、商务单人房、标准双人房、标准单人房等，房间干净整洁，服务态度好，部分房间配备台式电脑，是宾客商务、旅游的理想居所，温馨提示：酒店不能接待外宾。', '公共区域和部分房间提供wifi', '酒店提供免费停车位', '24小时热水', '叫醒服务', '无', '010-66072066', 'http://hotel.qunar.com/city/beijing_city/dt-12209/', 'http://userimg.qunar.com/imgs/201411/27/vUaycVBYlUFKaNssB480s.jpg');
